class MarioDoublePyramid:
    def __init__(self):
        self.height = 0

    def prompt_height(self):
        while True:
            try:
                self.height = int(input("Enter the height of the pyramids (1-8): "))
                if self.height < 1 or self.height > 8:
                    raise ValueError
                break
            except ValueError:
                print("Invalid input. Please enter a positive integer between 1 and 8.")

    def print_pyramids(self):
        for i in range(self.height):
            # Print left pyramid spaces
            print(" " * (self.height - i - 1), end="")

            # Print left pyramid hashes
            print("#" * (i + 1), end="")

            # Print gap between pyramids
            print("  ", end="")

            # Print right pyramid hashes
            print("#" * (i + 1))

    def run(self):
        self.prompt_height()
        self.print_pyramids()


mario = MarioDoublePyramid()
mario.run()


